import { useState, useEffect } from 'react';

import { FormGroup, FormControl, InputLabel, Input, Button, styled, Typography } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import { getProject, updateProject } from '../Service/Api';

const initialValue = {
    name: '',
    description: '',
    manager: ''
    
}

const Container = styled(FormGroup)`
    width: 50%;
    margin: 5% 0 0 25%;
    & > div {
        margin-top: 20px
`;

const UpdateProject = () => {
    const [project, setProject] = useState(initialValue);
    const { name, description, manager} = project;
    const { id } = useParams();
    
    let navigate = useNavigate();

    useEffect(() => {
        loadUserDetails();
    }, []);

    const loadUserDetails = async() => {
        const response = await getProject(id);
        setProject(response.data);
    }

    const editUserDetails = async() => {
        const response = await updateProject(id, project);
        navigate('/');
    }

    const onValueChange = (e) => {
        console.log(e.target.value);
        setProject({...project, [e.target.name]: e.target.value})
    }

    return (
        <Container injectfirst="true">
            <Typography variant="h4">Edit Information</Typography>
            <FormControl>
                <InputLabel htmlFor="my-input">Project Name</InputLabel>
                <Input onChange={(e) => onValueChange(e)} name='name' value={name} id="my-input"/>
            </FormControl>
            <FormControl>
                <InputLabel htmlFor="my-input">Description</InputLabel>
                <Input onChange={(e) => onValueChange(e)} name='username' value={description} id="my-input" />
            </FormControl>
            <FormControl>
                <InputLabel htmlFor="my-input">Manager</InputLabel>
                <Input onChange={(e) => onValueChange(e)} name='email' value={manager} id="my-input" />
            </FormControl>
            <FormControl>
                <Button variant="contained" color="primary"onClick={() => editUserDetails()}>Update Project</Button>
            </FormControl>
        </Container>
    )
}

export default UpdateProject
